            <el-alert
              center
              v-if="txOBJ.cStatus=='failed'"
              title="TRANSACTION FAILED"
              type="danger"
              :closable="false"
              show-icon>
            </el-alert>
            <el-alert
              center
              v-if="txOBJ.cStatus=='tx not found'"
              title="TRANSACTION NOT FOUND"
              type="warning"
              :closable="false"
              show-icon>
