<script>
import C from '../js/common.js'
import sqlite from '../db/db.js'
import QRCode from 'qrcode'
import VueAvatar from '@lossendae/vue-avatar'
import copy from 'copy-to-clipboard'

export default {
  name: 'account-page',
  components: {
    QRCode: QRCode,
    VueAvatar
  },
  data () {
    return {
      isRouterAlive: true,
      accountList: [],
      cardShow: true,
      accountInfoShow: false,
      accountSelect: {},
      balance: 0,
      activeIndex: '',
      visible2: false,
      transactionFromShow: false,
      editFromShow: false,
      passwordFromShow: false,
      formLabelWidth: '150px',
      handleSelect: '',
      widthP: '80%',
      coin_options: [{
        value: 'OG',
        label: 'OG'
      }],
      selectValue: 'OG',
      enterPassShow: false,
      tx: {
        password: ''
      },
      editData: {},
      testPasswordShow: true,
      changePasswordShow: false,
      password: {},
      visible: false,
      exportData: {},
      transactionData: [],
      now_address: '',
      showTxDetail: false,
      txOBJ: {},
      accountPageLoading: false
    }
  },
  created: function () {
    this.accountPageLoading = true
    setTimeout(() => {
      sqlite.query('SELECT * FROM usr').then((data) => {
        this.accountList = data.data
        console.log(this.accountList)
        this.accountPageLoading = false
      }).then().catch((err) => {
        console.log(err)
      })
    }, 600)
  },
  methods: {
    tableRowStyle ({ row, rowIndex }) {
      return 'color:#2d3e50; background: #e4f5f2;'
    },
    tableHeaderColor ({ row, column, rowIndex, columnIndex }) {
      if (rowIndex === 0) {
        return 'background: #e4f5f2; color:#2d3e50;'
      }
    },
    oneKeyCopy (data) {
      copy(data)
      this.$message({
        message: '"' + data + '" copied to clipboard.',
        type: 'success'
      })
    },
    random () {
      return Math.ceil(Math.random() * 10)
    },
    goIndex () {
      this.$router.push({ path: '/' })
    },
    goContract () {
      this.$router.push({ path: '/contract' })
    },
    goCreatAccount () {
      this.$router.push({ path: '/createAccount' })
    },
    goAccount () {
      this.$router.push({ path: '/account' })
    },
    goTransaction (account) {
      this.transactionFromShow = true
    },
    reflashTable () {
      C.checkTxStatus()
      setTimeout(() => {
        var sql = 'SELECT * from txHistory where cFrom == "' + this.accountSelect.address + '" order by ConfirmTime desc'
        sqlite.query(sql).then((data) => {
          console.log('txHistory', data.data)
          this.transactionData = data.data
        })
      }, 500)
    },
    txDetail (txHash) {
      console.log(txHash)
      this.showTxDetail = true
      var sql = 'SELECT * from txHistory where txHash == "' + txHash + '"'
      console.log(sql)
      sqlite.query(sql).then((data) => {
        console.log(data)
        this.txOBJ = data.data[0]
        this.txOBJ.txHashFrage01 = data.data[0].txHash.substring(0, 10)
        this.txOBJ.txHashFrage02 = data.data[0].txHash.substring(56)
        this.hashQrcode(data.data[0].txHash)
      }).then().catch((err) => {
        console.log(err)
      })
    },
    accountInfo (accountData) {
      this.cardShow = false
      this.accountInfoShow = true
      this.accountSelect = accountData
      console.log(this.accountSelect)
      var sql = 'SELECT * from txHistory where cFrom == "' + this.accountSelect.address + '" order by ConfirmTime desc'
      sqlite.query(sql).then((data) => {
        console.log('txHistory', data.data)
        this.transactionData = data.data
        var sql = 'SELECT * FROM usr WHERE address == "' + this.accountSelect.address + '"'
        return sqlite.query(sql)
      }).then((data) => {
        console.log(data.data)
        this.balance = data.data[0].balance_OG
        console.log(this.balance)
        return C.getBalance(data.data[0].address)
      }).then((data) => {
        this.balance = data.data.balance
        var sql = 'UPDATE usr SET balance_OG = ' + this.balance + ' where address = ' + '"' + this.accountSelect.address + '"'
        console.log(sql)
        return sqlite.execute(sql)
      }).then().catch((err) => {
        console.log(err)
      })
      this.reflashTable()
    },
    sinerConfirm (tx) {
      console.log('tx', tx)
      try {
        if (tx.password.length < 7) {
          this.$message({
            message: 'the passwords length must be more than 7',
            type: 'error'
          })
        }
        var decPri = C.decryptPrivKey(tx.password, this.accountSelect.privKey)
        console.log(decPri)
        if (decPri === '') {
          console.log('err')
          this.$message({
            message: 'WRONG PASSWORD! Please try again.',
            type: 'error'
          })
          this.tx.password = ''
        } else {
          tx.from = this.accountSelect.address
          tx.pubKey = this.accountSelect.pubKey
          tx.pubKey_raw = this.accountSelect.pubKey_raw
          tx.amount = Number(tx.amount)
          C.getNonce(tx.from).then((data) => {
            tx.nonce = data.data + 1
            var txParams = C.getTxParams(tx.from, tx.to, tx.amount, tx.pubKey, tx.pubKey_raw, tx.nonce)
            console.log('txParams', txParams)
            var signTarget = C.genRawTransaction(txParams)
            console.log('signTarget', signTarget)
            var signature = C.signRawTransaction(signTarget, decPri)
            console.log('signature', signature)

            var TX = C.makeUpTransaction(txParams, signature)
            console.log('TX', TX)
            return C.sendTransaction(TX)
          }).then((data) => {
            console.log(data)
            var result = JSON.parse(data.body)
            if (result.message) {
              this.$notify.error({
                title: 'transaction failed!',
                message: result.message
              })
            } else {
              this.$notify({
                title: 'transaction sended!',
                message: result.data,
                type: 'success'
              })
              this.transactionFromShow = false
              this.enterPassShow = false
              // save transaction
              tx.status = 'pending'
              C.saveTransaction(tx, result.data)
              // reload account page
              var sql = 'SELECT * from txHistory where cFrom == "' + this.accountSelect.address + '" order by ConfirmTime desc'
              sqlite.query(sql).then((data) => {
                this.transactionData = data.data
              })
              this.tx = {password: ''}
            }
          })
        }
      } catch (Error) {
        console.log(Error.toString())
        if (Error.toString() === 'Error: Malformed UTF-8 data') {
          this.$message({
            type: 'error',
            message: 'WRONG PASSWORD! Please try again.'
          })
          this.tx.password = ''
        } else {
          this.$message({
            type: 'error',
            message: Error.toString()
          })
          this.transactionFromShow = false
          this.enterPassShow = false
          this.tx = {password: ''}
        }
      }
    },
    goImportAccount () {
      this.$router.push({ path: '/importAccount' })
    },
    goEdit (account) {
      console.log(account)
      this.editData.name = account.account_name
      this.editFromShow = true
    },
    editAccount (data) {
      console.log(data)
      try {
        if (data.hint) {
          var sql1 = 'UPDATE usr SET account_name = "' + data.name + '", account_hint = "' + data.hint + '" where address = ' + '"' + this.accountSelect.address + '"'
          console.log(sql1)
          sqlite.execute(sql1).then((data) => {
            console.log(data)
            if (data.result === 'success') {
              this.$notify({
                title: 'EDIT SUCCESS!',
                type: 'success'
              })
            }
          })
        } else {
          var sql2 = 'UPDATE usr SET account_name = "' + data.name + '" where address = ' + '"' + this.accountSelect.address + '"'
          console.log(sql2)
          sqlite.execute(sql2).then((data) => {
            console.log(data)
            if (data.result === 'success') {
              this.$notify({
                title: 'EDIT SUCCESS!',
                type: 'success'
              })
            }
          })
          sqlite.query('SELECT * FROM usr where address = "' + this.accountSelect.address + '"').then((data) => {
            console.log(data)
            this.accountSelect.account_name = data.data[0].account_name
          })
        }
        this.editFromShow = false
      } catch (err) {
        console.log(err.toString())
        this.$message({
          type: 'error',
          message: err.toString()
        })
      }
    },
    goPassword () {
      this.passwordFromShow = true
    },
    exportAccount () {
      var a = C.decryptPrivKey(this.exportData.password, this.accountSelect.privKey)
      console.log(a)
      if (a === '') {
        console.log('err')
        this.$message({
          message: 'WRONG PASSWORD',
          type: 'error'
        })
      } else {
        var data = {
          name: this.accountSelect.account_name,
          address: this.accountSelect.address,
          secp_privKey: a,
          recoverPhrase: C.getRecoverPhrase(a)
        }
        C.layoutPDF(data)
        this.$notify({
          message: 'Account has been successfully exported!',
          type: 'success',
          duration: 40000
        })
        this.visible = false
      }
    },
    toEnterPass () {
      if (this.tx.to === '' || this.tx.to === undefined || this.tx.amount === '' || this.tx.amount === undefined) {
        this.$message({
          message: 'recipient address & amount can not be null',
          type: 'error'
        })
      } else {
        if (C.checkAddress(this.tx.to)) {
          this.enterPassShow = true
        } else {
          this.$message({
            message: 'Invalid format of recipient address. please check recipient address length and expect start with "0x"',
            type: 'error'
          })
        }
      }
    },
    testPassword () {
      var a = C.decryptPrivKey(this.password.testPassword, this.accountSelect.privKey)
      if (a === '') {
        console.log('err')
        this.$message({
          message: 'WRONG PASSWORD',
          type: 'error'
        })
      } else {
        this.$message({
          message: 'This password is correct',
          type: 'success'
        })
      }
    },
    changePassword () {

    },
    useqrcode (data) {
      var canvas = document.getElementById('qrcode')
      QRCode.toCanvas(canvas, data, function (error) {
        if (error) console.error(error)
        console.log('QRCode creat success!')
      })
    },
    hashQrcode (hash) {
      var canvas = document.getElementById('hashQrcode')
      QRCode.toCanvas(canvas, hash, function (error) {
        if (error) console.error(error)
        console.log('QRCode creat success!')
      })
    },
    deleteAccount (accountSelect) {
      console.log(accountSelect)
      C.deleteAccount(accountSelect.address)
      this.accountInfoShow = false
      this.cardShow = true
      this.visible2 = false
      sqlite.query('SELECT * FROM usr').then((data) => {
        this.accountList = data.data
        console.log('here', this.accountList)
      })
      this.$notify({
        title: 'Account deleted',
        message: 'account \n' + accountSelect.account_name + ' \n has been deleted.',
        type: 'warning'
      })
    },
    goBack () {
      this.accountInfoShow = false
      this.cardShow = true
      sqlite.query('SELECT * FROM usr').then((data) => {
        this.accountList = data.data
        console.log('here', this.accountList)
      })
    },
    reload () {
      this.isRouterAlive = false
      this.$nextTick(function () {
        this.isRouterAlive = true
      })
    }
  }
}
</script>